package br.com.xpto.inboundai.client;import br.com.xpto.inboundai.client.model.OpenAiRequest;import br.com.xpto.inboundai.client.model.OpenAiResponse;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;@FeignClient(name = "openai", url = "https://api.openai.com/v1", configuration = OpenAiConfig.class)public interface OpenAiClient {	@PostMapping("/chat/completions")	OpenAiResponse chat(@RequestBody OpenAiRequest request);}