package br.com.xpto.inboundai.client;import br.com.xpto.inboundai.client.model.OpenAiRequest;import br.com.xpto.inboundai.client.model.OpenAiResponse;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;@FeignClient(name = "openrouter", url = "${openrouter.url}", configuration = OpenRouterConfig.class)public interface OpenRouterClient {	@PostMapping("/chat/completions")	OpenAiResponse chat(@RequestBody OpenAiRequest request);}