package br.com.xpto.inboundai.client;import feign.RequestInterceptor;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class OpenRouterConfig {	@Value("${openrouter.api-key}")	private String apiKey;	@Bean	public RequestInterceptor requestInterceptor() {		return template -> {			template.header("Authorization", "Bearer " + apiKey);			template.header("Content-Type", "application/json");		};	}}