package br.com.xpto.inboundai.delegate;import lombok.extern.slf4j.Slf4j;import org.camunda.bpm.engine.delegate.DelegateExecution;import org.camunda.bpm.engine.delegate.JavaDelegate;import org.springframework.stereotype.Component;import java.time.LocalDateTime;@Slf4j@Component("checkLeadData")public class CheckLeadDataDelegate implements JavaDelegate {	@Override	public void execute(DelegateExecution delegateExecution) throws Exception {		log.info("checkLeadData with process instance id: {}", delegateExecution.getProcessInstanceId());		var payloadCompleted = (Boolean) delegateExecution.getVariable("payloadCompleted");		System.err.println("FINALIZOU O CHECK DATA: " + LocalDateTime.now() + " payload " + payloadCompleted);	}}