package br.com.xpto.inboundai.delegate;import lombok.extern.slf4j.Slf4j;import org.camunda.bpm.engine.delegate.DelegateExecution;import org.camunda.bpm.engine.delegate.JavaDelegate;import org.springframework.stereotype.Component;@Slf4j@Component("startConversation")public class StartConversation implements JavaDelegate {	@Override	public void execute(DelegateExecution delegateExecution) throws Exception {		log.info("Starting conversation with process instance id: {}", delegateExecution.getProcessInstanceId());		var payloadCompleted = (Boolean) delegateExecution.getVariable("payloadCompleted");		System.err.println("CHEGOU NO START CONVERSATION: " + payloadCompleted);	}}