package br.com.xpto.inboundai.model;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.mongodb.core.mapping.Document;import java.io.Serial;import java.io.Serializable;@Data@AllArgsConstructor@NoArgsConstructor@Builder@Document(collection = "leads")public class Lead implements Serializable {	@Serial	private static final long serialVersionUID = -5735357817363777027L;	private String id;	private String name;	private String email;	private Company company;	private Qualification qualification;	@Data	@AllArgsConstructor	@NoArgsConstructor	@Builder	private static class Company implements Serializable {		@Serial		private static final long serialVersionUID = 1L;		private String name;		private String cnpj;		private AddressCompany addressCompany;		private String phone;		private String website;		private String email;		private String description;		private String industry;		private String size;		private String revenue;		private String foundedYear;		private String headquartersLocation;		@Data		@AllArgsConstructor		@NoArgsConstructor		@Builder		private static class AddressCompany implements Serializable {			@Serial			private static final long serialVersionUID = -26493609747900193L;			private String street;			private String city;			private String state;			private String country;			private String zipCode;		}	}	@Data	@AllArgsConstructor	@NoArgsConstructor	@Builder	private static class Qualification implements Serializable {		@Serial		private static final long serialVersionUID = 1L;		private String status;		private String score;		private String source;		private String notes;		private String lastContacted;		private String nextFollowUp;		private String assignedTo;		private String tags;	}}