package br.com.xpto.inboundai.model;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotNull;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serial;import java.io.Serializable;@Data@AllArgsConstructor@NoArgsConstructor@Builderpublic class MessageBody implements Serializable {	@Serial	private static final long serialVersionUID = 6299943802957676930L;	@NotNull(message = "The 'to' field must not be null")	private String to;	@NotBlank(message = "The 'from' field must not be null")	private String from;	@NotNull(message = "The 'content' field must not be null")	private String content;}