package br.com.xpto.inboundai.service;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.camunda.bpm.engine.RuntimeService;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.Map;import java.util.UUID;@Slf4j@Service@RequiredArgsConstructorpublic class CamundaServiceImpl implements CamundaService {	@Value("${process.main-workflow}")	private String processId;	private final RuntimeService runtimeService;	@Override	public void startMainWorkflow(String businessKey) {		if (businessKey == null || businessKey.isEmpty()) {			businessKey = generateBusinessKey();		}		Map<String, Object> variables = buildArgs(businessKey);		runtimeService.startProcessInstanceByKey(processId, businessKey, variables);		log.info("Started process '{}' with businessKey '{}'", processId, businessKey);	}	private Map<String, Object> buildArgs(String businessKey) {		Map<String, Object> args = new HashMap<>();		args.put("businessKey", businessKey);		args.put("gabriel", "test");		return args;	}	private String generateBusinessKey() {		return "MAIN-" + UUID.randomUUID();	}}