package br.com.xpto.inboundai.workflow;import br.com.xpto.inboundai.workflow.context.GlobalContext;import br.com.xpto.inboundai.workflow.step.WorkflowStep;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Component;import java.util.List;@Component@RequiredArgsConstructorpublic class InboundWorkflow {	private final List<WorkflowStep> steps;	public void start(GlobalContext context) {		for (WorkflowStep step : steps) {			step.execute(context);		}	}}