package br.com.xpto.inboundai.workflow.config;import br.com.xpto.inboundai.workflow.InboundWorkflow;import br.com.xpto.inboundai.workflow.context.GlobalContext;import br.com.xpto.inboundai.workflow.step.WorkflowStep;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import java.util.List;@Configurationpublic class WorkflowConfiguration {	@Bean	public InboundWorkflow inboundWorkflow(List<WorkflowStep> steps) {		return new InboundWorkflow(steps);	}	@Bean	public GlobalContext globalContext() {		return new GlobalContext();	}}