package br.com.xpto.inboundai.workflow.context;import java.util.HashMap;import java.util.Map;public class GlobalContext {	private final Map<String, Object> data = new HashMap<>();	public void put(String key, Object value) {		data.put(key, value);	}	public Object get(String key) {		return data.get(key);	}	public <T> T get(String key, Class<T> clazz) {		return clazz.cast(data.get(key));	}}